import React, { Component } from "react";
import axios from "axios";


const textStatus = () => {
    return (
        <div className="col-12">
            {this.state.status == 'success' ? <div className="alert alert-success" role="alert">{state.message}</div> : ''}
            {this.state.status == 'error' ? <div className="alert alert-danger" role="alert">{state.message}</div> : ''}
        </div>
    )
}
const htmlReset = () => {
    return (
        <div className="col-12">
            <button type="button" className="btn btn-outline-primary" onClick={handleClick()}>Reset (TRUNCATE)</button>
        </div>
    )
}

export default class Home extends Component {
    // constructor(props) {
    //     super(props);
    //     this.state = { status: null, message: '' };
    //     this.handleClick = this.handleClick.bind(this);
    //     console.log(htmlReset);
    // }

    state = {status:null, message: ''}

    componentDidMount() {
        this.setState({ status: null, message: '' });
    }

    handleClick() {
        this.setState({ status: null, message: '' })
        let url = "http://localhost:5000/truncate"
        axios.post(url).then((res) => {
            console.log(res);
            this.setState({ status: 'success', message: 'TRUNCATE all success' })
        }).catch((error) => {
            this.setState({ status: 'error', message: 'Fail truncate.' })
        });
    }

    render() {

        return (
            <div className="container">
                <div className="row py-3">
                    { textStatus }
                    { htmlReset }
                </div>
            </div>
        );
    }
}
