import React, { Component } from "react";
import axios from "axios";

const status = null;

export default class Home extends Component {

    handleClick() {
        console.log("Click");
        let url = "http://localhost:5000/truncate"
        axios.post(url).then((res)=>{
            console.log(res);
            status = 'success';
        }).catch((error)=>{
            console.log(error);
            status = 'error';
        });
    }

    render() {
        const status = null;
        return (
            <div className="container">
                <div className="row py-3">
                    <div className="col-12">
                        { status=='success' ? <p className="alert alert-ouline-success" id="success"></p> : ''}
                        { status=='error' ? <p className="alert alert-ouline-error" id="error"></p> : ''}
                    </div>
                    <div className="col-12">
                        <button type="button" className="btn btn-outline-primary" onClick={this.handleClick}>Reset (TRUNCATE)</button>
                    </div>
                </div>
            </div>
        );
    }
}
