import React, { Component } from "react";
import axios from "axios";

export default class Setdate extends Component {
  constructor(props) {
    super(props);
    this.state = { status: null, message: "", date: '' };
    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.setState({ status: null, message: "", date: '' });
  }

  handleChange(e) {
      this.setState({date: e.target.value});
  }

  handleClick() {
    this.setState({ status: null, message: "" });
    let url = "http://localhost:5000/truncate";
    let parameter = {date: this.state.date}
    axios
      .post(url)
      .then((res) => {
        console.log(res);
        // this.setState({ status: "success", message: "TRUNCATE all success" });
      })
      .catch((error) => {
        // this.setState({ status: "error", message: "Fail truncate." });
      });
  }

  render() {
    return (
      <div>
        {this.state.status == "success" ? (
          <div className="alert alert-success" role="alert">
            {this.state.message}
          </div>
        ) : (
          ""
        )}
        {this.state.status == "error" ? (
          <div className="alert alert-danger" role="alert">
            {this.state.message}
          </div>
        ) : (
          ""
        )}
        <div className="input-group mb-3">
          <input type="date" className="form-control" onChange={this.handleChange} />
          <div className="input-group-append">
            <button className="btn btn-outline-primary" type="button" onClick={this.handleClick}>
              Update
            </button>
          </div>
        </div>
      </div>
    );
  }
}
