import React, { Component } from "react";
import axios from "axios";



export default class Home extends Component {
    constructor(props) {
        super(props);
        this.state = { status: null, message: '' };
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        this.setState({ status: null, message: '' });
    }

    handleClick() {
        this.setState({ status: null, message: '' })
        let url = "http://localhost:5000/truncate"
        axios.post(url).then((res) => {
            console.log(res);
            this.setState({ status: 'success', message: 'TRUNCATE all success' })
        }).catch((error) => {
            this.setState({ status: 'error', message: 'Fail truncate.' })
        });
    }

    render() {


        const htmlReset = () => {
            return (
                <div className="col-12">
                    <button type="button" className="btn btn-outline-primary" onClick={this.handleClick}>Reset (TRUNCATE)</button>
                </div>
            )
        }

        return (
            <div className="container">
                <div className="row py-3">
                    { htmlReset }
                </div>
            </div>
        );
    }
}
