import React, { Component } from "react";
import axios from "axios";
import Delete from "./Delete";
import Setdate from "./Setdate";

export default class Home extends Component {
    constructor(props) {
        super(props);
        this.state = { status: null, message: "" };
    }

    componentDidMount() {
        this.setState({ status: null, message: "" });
    }

    handleClick() {
        this.setState({ status: null, message: "" });
        let url = "http://localhost:5000/truncate";
        axios
            .post(url)
            .then((res) => {
                console.log(res);
                this.setState({ status: "success", message: "TRUNCATE all success" });
            })
            .catch((error) => {
                this.setState({ status: "error", message: "Fail truncate." });
            });
    }

    render() {
        return (
            <div className="container">
                <div className="row py-3">
                    <div className="col-12">
                        {this.state.status == "success" ? (
                            <div className="alert alert-success" role="alert">
                                {this.state.message}
                            </div>
                        ) : (
                                ""
                            )}
                        {this.state.status == "error" ? (
                            <div className="alert alert-danger" role="alert">
                                {this.state.message}
                            </div>
                        ) : (
                                ""
                            )}
                    </div>
                    <div className="col-12">
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Turncate all for reset new import data</td>
                                    <td>
                                        <Delete />
                                    </td>
                                </tr>
                                <tr>
                                    <td>Set date</td>
                                    <td><Setdate /></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}
